import React, { useEffect, useRef } from "react";
import * as d3 from "d3";

const ScatterPlot = ({ data, xFeature = "SepalWidthCm", yFeature = "PetalWidthCm" }) => {
    const svgRef = useRef(null);

    useEffect(() => {
        if (!data || !data.points || !Array.isArray(data.points)) return;

        const svg = d3.select(svgRef.current);
        svg.selectAll("*").remove();

        const width = 500;
        const height = 400;
        const margin = { top: 20, right: 20, bottom: 40, left: 40 };

        const g = svg
            .attr("width", width)
            .attr("height", height)
            .append("g")
            .attr("transform", `translate(${margin.left}, ${margin.top})`);

        // Extract x and y values from features
        const xValues = data.points.map(d => d.features[xFeature]);
        const yValues = data.points.map(d => d.features[yFeature]);

        const xScale = d3
            .scaleLinear()
            .domain(d3.extent(xValues))
            .range([0, width - margin.left - margin.right]);

        const yScale = d3
            .scaleLinear()
            .domain(d3.extent(yValues))
            .range([height - margin.top - margin.bottom, 0]);

        const colorScale = d3
            .scaleOrdinal(d3.schemeCategory10)
            .domain([...new Set(data.points.map(d => d.actual_label))]);

        // Axes
        g.append("g")
            .attr("transform", `translate(0, ${height - margin.top - margin.bottom})`)
            .call(d3.axisBottom(xScale))
            .append("text")
            .attr("x", (width - margin.left - margin.right) / 2)
            .attr("y", 30)
            .attr("fill", "black")
            .style("text-anchor", "middle")
            .text(xFeature);

        g.append("g")
            .call(d3.axisLeft(yScale))
            .append("text")
            .attr("transform", "rotate(-90)")
            .attr("y", -margin.left)
            .attr("x", -(height - margin.top - margin.bottom) / 2)
            .attr("dy", "1em")
            .attr("fill", "black")
            .style("text-anchor", "middle")
            .text(yFeature);

        // Points
        g.selectAll("circle")
            .data(data.points)
            .enter()
            .append("circle")
            .attr("cx", d => xScale(d.features[xFeature]))
            .attr("cy", d => yScale(d.features[yFeature]))
            .attr("r", 5)
            .attr("fill", d => colorScale(d.actual_label))
            .attr("stroke", "black")
            .attr("stroke-width", 0.5)
            .append("title") // Tooltip
            .text(d => `ID: ${d.Point_ID}\nActual: ${d.actual_label}\nPredicted: ${d.predicted_label}`);

        // Legend
        const legend = g.append("g")
            .attr("transform", `translate(${width - margin.left - margin.right - 100}, 0)`);

        const uniqueLabels = [...new Set(data.points.map(d => d.actual_label))];
        uniqueLabels.forEach((label, i) => {
            const legendRow = legend.append("g")
                .attr("transform", `translate(0, ${i * 20})`);

            legendRow.append("rect")
                .attr("width", 10)
                .attr("height", 10)
                .attr("fill", colorScale(label));

            legendRow.append("text")
                .attr("x", 15)
                .attr("y", 10)
                .attr("fill", "black")
                .text(label);
        });

    }, [data, xFeature, yFeature]);

    return (
        <div style={{ width: "100%", height: "400px" }}>
            <svg ref={svgRef}></svg>
        </div>
    );
};

export default ScatterPlot;
