import React, { useEffect, useRef } from "react";
import * as d3 from "d3";

const HierarchicalGraph = ({ jsonData, setHoveredCoordinates }) => {
  const graphRef = useRef(null);

  useEffect(() => {
    if (!jsonData || jsonData.length === 0) return;

    const coordinateKeys = Object.keys(jsonData[0]).filter(
      (key) => key !== "Cluster" && key !== "Bead"
    );

    const calculateCentroid = (points) => {
      const n = points.length;
      const sum = points.reduce((acc, point) => {
        coordinateKeys.forEach((key) => {
          acc[key] = (acc[key] || 0) + point[key];
        });
        return acc;
      }, {});
      Object.keys(sum).forEach((key) => (sum[key] /= n));
      return sum;
    };

    const assignBitVector = (point) => {
      return coordinateKeys
        .map((key) => (point[key] >= 0 ? "1" : "0"))
        .join("");
    };

    const clusters = d3.group(jsonData, (d) => d.Cluster);
    const beads = d3.group(jsonData, (d) => `${d.Cluster}-${d.Bead}`);

    const rootCentroid = calculateCentroid(jsonData);

    const clusterCentroids = Array.from(clusters, ([cluster, points]) => ({
      cluster,
      centroid: calculateCentroid(points),
    }));

    const beadCentroids = Array.from(beads, ([key, points]) => {
      const [cluster, bead] = key.split("-");
      return {
        cluster,
        bead,
        centroid: calculateCentroid(points),
        points,
      };
    });

    const nodes = [
      { id: "root", ...rootCentroid, bitVector: assignBitVector(rootCentroid) },
    ];

    const links = [];

    clusterCentroids.forEach(({ cluster, centroid }) => {
      nodes.push({
        id: `cluster-${cluster}`,
        ...centroid,
        bitVector: assignBitVector(centroid),
      });
      links.push({ source: "root", target: `cluster-${cluster}` });
    });

    beadCentroids.forEach(({ cluster, bead, centroid, points }) => {
      nodes.push({
        id: `bead-${cluster}-${bead}`,
        ...centroid,
        bitVector: assignBitVector(centroid),
      });
      links.push({
        source: `cluster-${cluster}`,
        target: `bead-${cluster}-${bead}`,
      });

      points.forEach((point, index) => {
        const pointId = `point-${cluster}-${bead}-${index}`;
        nodes.push({
          id: pointId,
          ...point,
          bitVector: assignBitVector(point),
        });
        links.push({ source: `bead-${cluster}-${bead}`, target: pointId });
      });
    });

    const container = d3.select(graphRef.current.parentNode);
    const width = container.node().getBoundingClientRect().width;
    const height = container.node().getBoundingClientRect().height;

    const svg = d3
      .select(graphRef.current)
      .attr("width", width)
      .attr("height", height)
      .attr("viewBox", `0 0 ${width} ${height}`)
      .attr("preserveAspectRatio", "xMidYMid meet");

    svg.selectAll("*").remove();

    // Initialize quadtree
    const quadtree = d3
      .quadtree()
      .x((d) => d.x || 0) // Default x-coordinate
      .y((d) => d.y || 0) // Default y-coordinate
      .addAll(nodes);

    const findPointsInQuadrant = (bitVector) => {
      const matchingPoints = [];
      quadtree.visit((node, x0, y0, x1, y1) => {
        if (!node.length) {
          const d = node.data;
          if (d.bitVector.startsWith(bitVector)) {
            matchingPoints.push(d);
          }
        }
        return false;
      });
      return matchingPoints;
    };

    const simulation = d3
      .forceSimulation(nodes)
      .force(
        "link",
        d3.forceLink(links).id((d) => d.id)
      )
      .force("charge", d3.forceManyBody().strength(-100))
      .force("center", d3.forceCenter(width / 2, height / 2))
      .on("tick", () => {
        link
          .attr("x1", (d) => d.source.x)
          .attr("y1", (d) => d.source.y)
          .attr("x2", (d) => d.target.x)
          .attr("y2", (d) => d.target.y);

        node.attr("cx", (d) => d.x).attr("cy", (d) => d.y);
      });

    const link = svg
      .append("g")
      .attr("class", "links")
      .selectAll("line")
      .data(links)
      .enter()
      .append("line")
      .attr("stroke", "#aaa")
      .attr("stroke-width", 2);

    const node = svg
      .append("g")
      .attr("class", "nodes")
      .selectAll("circle")
      .data(nodes)
      .enter()
      .append("circle")
      .attr("r", 5)
      .attr("fill", (d) => {
        if (d.id === "root") return "red";
        if (d.id.startsWith("cluster")) return "blue";
        if (d.id.startsWith("bead")) return "green";
        return "gray";
      })
      .on("mouseover", (event, d) => {
        const coordinates = {};
        coordinateKeys.forEach((key) => {
          coordinates[key] = d[key];
        });
        setHoveredCoordinates({
          ...coordinates,
          id: d.id,
          bitVector: d.bitVector,
        });
      });

    // Highlight quadrant
    svg.on("click", () => {
      const positivePoints = findPointsInQuadrant("1");
      console.log("Positive Points: ", positivePoints);
    });
  }, [jsonData, setHoveredCoordinates]);

  return <svg ref={graphRef} style={{ width: "100%", height: "100%" }}></svg>;
};

export default HierarchicalGraph;
