import React, { useEffect, useRef } from "react";
import * as d3 from "d3";

const HierarchicalGraph = ({ jsonData }) => {
  const graphRef = useRef(null);

  useEffect(() => {
    if (!jsonData || Object.keys(jsonData).length === 0) return;

    const subspaces = Object.keys(jsonData);

    // Sort subspaces by dimension in *descending* order
    subspaces.sort((a, b) => b.length - a.length);

    const pointsData = subspaces.map((key) => ({
      key,
      points: jsonData[key],
      dimensions: key.length, // Length of the key determines the dimensions
      subspaceId: key, // Store the subspace identifier
    }));

    const svg = d3.select(graphRef.current);
    const width = 600;
    const height = 600;
    const margin = 20;

    svg.selectAll("*").remove(); // Clear previous content

    const maxSquareSize = Math.min(width, height) - 2 * margin;

    const g = svg
      .attr("width", width)
      .attr("height", height)
      .append("g")
      .attr("transform", `translate(${width / 2}, ${height / 2})`);

    // Start with the *smallest* square and increase size for each subsequent subspace
    pointsData.forEach((subspace, index) => {
      const squareSize = maxSquareSize * ((index + 1) / subspaces.length);
      const sectors = 2 ** subspace.dimensions; // Number of sectors

      // Define a color scale (you can customize these colors)
      const colorScale = d3
        .scaleOrdinal()
        .range(["#FFD700", "#FF69B4", "#33B5E5"]); // Gold, Pink, Light Blue

      // Determine color based on subspace dimension
      const subspaceColor = colorScale(
        subspace.dimensions % colorScale.range().length
      );

      // Draw square for the subspace
      g.append("rect")
        .attr("x", -squareSize / 2)
        .attr("y", -squareSize / 2)
        .attr("width", squareSize)
        .attr("height", squareSize)
        .attr("stroke", "black") // Make the square outline clearer
        .attr("fill", subspaceColor)
        .attr("fill-opacity", 0.2) // Reduce opacity to see sector lines better
        .attr("stroke-width", 2); // Thicker stroke for better visibility

      // Draw sector lines (dividing the square into equal parts)
      if (subspace.dimensions > 0) {
        // Only draw lines if there are sectors to divide
        for (let i = 0; i < sectors; i++) {
          const angle = (2 * Math.PI * i) / sectors;
          const x1 = (squareSize / 2) * Math.cos(angle);
          const y1 = (squareSize / 2) * Math.sin(angle);

          // g.append("line")
          //   .attr("x1", 0)
          //   .attr("y1", 0)
          //   .attr("x2", x1)
          //   .attr("y2", y1)
          //   .attr("stroke", "gray") // Slightly darker lines
          //   .attr("stroke-width", 1);

          // Add sector labels
          const labelX = (squareSize / 2 + 10) * Math.cos(angle); // Place label outside the square
          const labelY = (squareSize / 2 + 10) * Math.sin(angle);

          g.append("text")
            .attr("x", labelX)
            .attr("y", labelY + 5) // Adjust for vertical alignment
            .attr("text-anchor", "middle")
            .attr("dominant-baseline", "middle")
            .style("font-size", "10px")
            .style("fill", "black");
            // .text(
            //   subspace.subspaceId +
            //     i.toString(2).padStart(subspace.dimensions, "0")
            // );
        }
      }

      // Scale for plotting points, now relative to sector
      const sectorRadius = squareSize / 2;

      // Plot points within their respective square and sector
      subspace.points.forEach((point) => {
        const bitPattern = point.Point_ID.toString(2).padStart(
          subspace.dimensions,
          "0"
        );
        const sectorIndex = parseInt(bitPattern, 2);
        const sectorAngleStart = (2 * Math.PI * sectorIndex) / sectors;
        const sectorAngleEnd = (2 * Math.PI * (sectorIndex + 1)) / sectors;

        // Place points randomly inside the sector, further from the center
        const minRadius = sectorRadius * 0.2;
        const randomRadius =
          minRadius + Math.random() * (sectorRadius - minRadius);
        const randomAngle =
          sectorAngleStart +
          Math.random() * (sectorAngleEnd - sectorAngleStart);

        const x = randomRadius * Math.cos(randomAngle);
        const y = randomRadius * Math.sin(randomAngle);

        // Check if the point lies within this subspace's square.  This is critical
        // to prevent points from bleeding into other squares.
        if (Math.abs(x) <= squareSize / 2 && Math.abs(y) <= squareSize / 2) {
          g.append("circle")
            .attr("cx", x)
            .attr("cy", y)
            .attr("r", 4)
            .attr("fill", "black")
            .attr("stroke", "white")
            .attr("stroke-width", 0.5)
            .append("title");
            // .text(
            //   `ID: ${point.Point_ID}, Binary: ${bitPattern}, Subspace: ${subspace.subspaceId}`
            // );
        } else {
          console.warn(
            `Point ${point.Point_ID} is outside the subspace ${subspace.subspaceId}`
          ); // Debugging
        }
      });
    });
  }, [jsonData]);

  return (
    <svg
      ref={graphRef}
      style={{ width: "600px", height: "600px", border: "1px solid black" }}
    ></svg>
  );
};

