import React, { useEffect, useRef } from "react";
import * as d3 from "d3";

const HierarchicalGraph = ({ jsonData }) => {
  const graphRef = useRef(null);

  useEffect(() => {
    if (!jsonData || Object.keys(jsonData).length === 0) return;

    const subspaces = Object.keys(jsonData);
    subspaces.sort((a, b) => a.length - b.length);

    const pointsData = subspaces.map((key) => ({
      key,
      points: jsonData[key],
      dimensions: key.length,
      subspaceId: key,
    }));

    const svg = d3.select(graphRef.current);
    const width = 600;
    const height = 600;
    const margin = 20;
    const maxRadius = Math.min(width, height) / 2 - margin;

    svg.selectAll("*").remove();
    const g = svg
      .attr("width", width)
      .attr("height", height)
      .append("g")
      .attr("transform", `translate(${width / 2}, ${height / 2})`);

    pointsData.forEach((subspace, index) => {
      const radius = maxRadius * ((index + 1) / subspaces.length);
      const sectors = 2 ** subspace.dimensions;

      g.append("circle")
        .attr("r", radius)
        .attr("stroke", "black")
        .attr("fill", "none")
        .attr("stroke-width", 2);

      // Draw lines for divisions
      if (subspace.dimensions === 1) {
        g.append("line")
          .attr("x1", -radius)
          .attr("y1", 0)
          .attr("x2", radius)
          .attr("y2", 0)
          .attr("stroke", "black")
          .attr("stroke-width", 1);
      } else {
        for (let i = 0; i < sectors; i++) {
          const angle = (2 * Math.PI * i) / sectors;
          const x1 = radius * Math.cos(angle);
          const y1 = radius * Math.sin(angle);
          g.append("line")
            .attr("x1", 0)
            .attr("y1", 0)
            .attr("x2", x1)
            .attr("y2", y1)
            .attr("stroke", "black")
            .attr("stroke-width", 1);
        }
      }

      subspace.points.forEach((point) => {
        let bitPattern = "";
        point.Coordinates.forEach((coord) => {
          bitPattern += coord >= 0 ? "1" : "0";
        });

        const sectorIndex = parseInt(bitPattern, 2);
        const sectorAngle = (2 * Math.PI * sectorIndex) / sectors;
        const pointRadius = radius * 0.8;
        const x = pointRadius * Math.cos(sectorAngle);
        const y = pointRadius * Math.sin(sectorAngle);

        g.append("circle")
          .attr("cx", x)
          .attr("cy", y)
          .attr("r", 4)
          .attr("fill", "black")
          .attr("stroke", "white")
          .attr("stroke-width", 0.5)
          .append("title")
          .text(`ID: ${point.Point_ID}, Binary: ${bitPattern}`);
      });
    });
  }, [jsonData]);

  return <svg ref={graphRef} style={{ width: "600px", height: "600px" }}></svg>;
};

export default HierarchicalGraph;
