      console.log("Last ring dimensions:", lastRing.dimensions);
      console.log("Sectors count:", sectorsCount);
      console.log("Last ring points:", lastRingPoints);

      // Count points per sector
      const pointCounts = new Array(sectorsCount).fill(0);
      lastRingPoints.forEach((point, idx) => {
        const pointData = Object.entries(point).filter(([key]) => key !== "Point_ID");
        const bitVector = pointData.map(([_, coord]) => (coord >= 0 ? 1 : 0)).join("");
        const sectorIndex = Math.min(parseInt(bitVector, 2) || 0, sectorsCount - 1);
        pointCounts[sectorIndex]++;
        console.log(`Point ${idx}: bitVector=${bitVector}, sectorIndex=${sectorIndex}`);
      });
      console.log("Point counts:", pointCounts);

      // Calculate proportional angles
      const totalAngle = 2 * Math.PI;
      const totalPoints = Math.max(1, pointCounts.reduce((sum, count) => sum + count, 0));
      const baseAngle = totalAngle / sectorsCount; // Equal angle as fallback
      const minAngle = Math.PI / 180; // 1 degree minimum
      const remainingAngle = totalAngle - sectorsCount * minAngle;

      const sectorAngles = pointCounts.map(count => {
        const proportionalAngle = count > 0 ? (count / totalPoints) * remainingAngle : 0;
        return minAngle + proportionalAngle;
      });
      console.log("Sector angles (outer ring):", sectorAngles);

      // Verify total angle
      const totalCalculatedAngle = sectorAngles.reduce((sum, angle) => sum + angle, 0);
      console.log("Total calculated angle (should be ~2π):", totalCalculatedAngle);

      // Propagate to inner rings
      const ringAngles = [sectorAngles];
      for (let i = pointsData.length - 2; i >= 0; i--) {
        const prevAngles = ringAngles[0];
        const sectorCount = Math.max(1, 2 ** (i + 1));
        const currAngles = new Array(sectorCount).fill(0);
        for (let j = 0; j < sectorCount; j++) {
          currAngles[j] = (prevAngles[j * 2] || 0) + (prevAngles[j * 2 + 1] || 0);
        }
        ringAngles.unshift(currAngles);
        console.log(`Ring ${i} angles:`, currAngles);
      }

      console.log("Final ring angles:", ringAngles);
      return ringAngles;
