import React, { useEffect, useRef } from "react";
import * as d3 from "d3";

const ParallelCoordinates = ({ data, labelsData }) => {
    const svgRef = useRef(null);

    useEffect(() => {
        if (!data || !data.points || !Array.isArray(data.points)) return;

        const svg = d3.select(svgRef.current);
        svg.selectAll("*").remove();

        const width = 500;
        const height = 400;
        const margin = { top: 20, right: 20, bottom: 20, left: 20 };

        const dimensions = Object.keys(data.points[0]).filter(k => k !== "id");

        const yScales = {};
        dimensions.forEach(dim => {
            yScales[dim] = d3
                .scaleLinear()
                .domain(d3.extent(data.points, d => d[dim]))
                .range([height - margin.top - margin.bottom, 0]);
        });

        const xScale = d3
            .scalePoint()
            .domain(dimensions)
            .range([0, width - margin.left - margin.right]);

        const colorScale = d3
            .scaleOrdinal(d3.schemeCategory10)
            .domain(Object.keys(labelsData?.labels || {}));

        const getLabelColor = (id) => {
            if (!labelsData || !labelsData.labels) return "gray";
            for (const [label, ids] of Object.entries(labelsData.labels)) {
                if (ids.includes(Number(id))) return colorScale(label);
            }
            return "gray";
        };

        const g = svg
            .attr("width", width)
            .attr("height", height)
            .append("g")
            .attr("transform", `translate(${margin.left}, ${margin.top})`);

        // Axes
        g.selectAll(".axis")
            .data(dimensions)
            .enter()
            .append("g")
            .attr("class", "axis")
            .attr("transform", d => `translate(${xScale(d)}, 0)`)
            .each(function (d) {
                d3.select(this).call(d3.axisLeft(yScales[d]));
            })
            .append("text")
            .attr("y", -10)
            .attr("text-anchor", "middle")
            .attr("fill", "black")
            .text(d => d);

        // Lines
        const line = d3.line();
        g.selectAll(".line")
            .data(data.points)
            .enter()
            .append("path")
            .attr("class", "line")
            .attr("d", d =>
                line(dimensions.map(dim => [xScale(dim), yScales[dim](d[dim])]))
            )
            .attr("stroke", d => getLabelColor(d.id))
            .attr("stroke-width", 1)
            .attr("fill", "none")
            .attr("opacity", 0.5);
    }, [data, labelsData]);

    return (
        <div style={{ width: "100%", height: "400px" }}>
            <svg ref={svgRef}></svg>
        </div>
    );
};

export default ParallelCoordinates;
