import React, { useEffect, useRef, useState } from "react";
import * as d3 from "d3";

const HierarchicalGraph = ({ jsonData, labelsData, setHoveredCoordinates, ringVisibility }) => {
  const graphRef = useRef(null);
  const [viewMode, setViewMode] = useState("normal");

  useEffect(() => {
    if (!jsonData || typeof jsonData !== "object" || Object.keys(jsonData).length === 0) {
      console.error("Invalid or empty jsonData:", jsonData);
      return;
    }

    if (!labelsData || typeof labelsData !== "object") {
      console.error("Invalid labelsData:", labelsData);
      return;
    }

    // Clear previous graph and tooltip
    const svg = d3.select(graphRef.current);
    svg.selectAll("*").remove();
    d3.select("body .tooltip").remove();

    const width = 800;
    const height = 800;
    const margin = 20;
    const maxRadius = Math.min(width, height) / 2 - margin;

    const g = svg
      .attr("width", width)
      .attr("height", height)
      .append("g")
      .attr("transform", `translate(${width / 2}, ${height / 2})`);

    // Create tooltip once
    const tooltip = d3
      .select("body")
      .append("div")
      .attr("class", "tooltip")
      .style("position", "absolute")
      .style("visibility", "hidden")
      .style("background-color", "rgba(0, 0, 0, 0.7)")
      .style("color", "white")
      .style("padding", "5px")
      .style("border-radius", "4px")
      .style("font-size", "12px");

    // Color functions
    const colorScale = d3.scaleOrdinal(d3.schemeCategory10)
      .domain(Object.keys(labelsData.labels || {}));

    const getRingColor = (index) => {
      const totalRings = Object.keys(jsonData).length;
      const colorScaleInd = d3.scaleSequential(d3.interpolatePlasma).domain([totalRings, 0]);
      return d3.color(colorScaleInd(index));
    };

    const getSectorColor = (index, sectorIndex) => {
      const baseColor = d3.hsl(getRingColor(index));
      const isPositive = sectorIndex % 2 === 0;
      return d3.hsl(baseColor.h, baseColor.s, isPositive ? 0.75 : 0.35).toString();
    };

    const getLabelColor = (pointId) => {
      if (!labelsData || !labelsData.labels) return "gray";
      for (const label of Object.keys(labelsData.labels)) {
        const pointList = labelsData.labels[label];
        if (Array.isArray(pointList) && pointList.includes(Number(pointId))) {
          return colorScale(label);
        }
      }
      return "gray";
    };

    // Sort subspaces by dimension (length of key)
    const subspaces = Object.keys(jsonData);
    subspaces.sort((a, b) => a.length - b.length);

    // Process data once
    const pointsData = subspaces.map((key) => {
      // Pre-process points to avoid repeated calculations
      const processedPoints = (jsonData[key] || []).map(point => {
        const pointData = Object.entries(point).filter(([k]) => k !== "Point_ID");
        const bitVector = pointData.map(([_, coord]) => (coord >= 0 ? 1 : 0)).join("");
        return {
          ...point,
          bitVector,
          sectorIndex: parseInt(bitVector, 2)
        };
      });

      return {
        key,
        points: processedPoints,
        dimensions: key.length,
      };
    });

    const ringLabels = subspaces.map((_, i) => String.fromCharCode(65 + i));

    // Memory-efficient angle calculation
    const calculateSectorAngles = () => {
      const sectorAngles = {};
      const ringsOrderedByDimension = [...pointsData].sort((a, b) => b.dimensions - a.dimensions);

      // Process each ring, starting from the outermost
      ringsOrderedByDimension.forEach(ringData => {
        const key = ringData.key;
        const sectors = 2 ** ringData.dimensions;
        const sectorCounts = new Array(sectors).fill(0);

        // Count points in each sector
        ringData.points.forEach(point => {
          const sectorIndex = Math.min(point.sectorIndex, sectors - 1);
          sectorCounts[sectorIndex]++;
        });

        // Calculate angles
        const minAngle = 0.02 * (Math.PI * 2) / sectors;
        const totalPoints = ringData.points.length || 1;
        const emptySectors = sectorCounts.filter(count => count === 0).length;
        const remainingAngle = 2 * Math.PI - (minAngle * emptySectors);

        // Store only what's needed
        const angles = [];
        let currentAngle = Math.PI / 2; // Start from top

        for (let i = 0; i < sectors; i++) {
          const angle = sectorCounts[i] === 0 ? minAngle : (sectorCounts[i] / totalPoints) * remainingAngle;
          angles.push({ start: currentAngle, end: currentAngle + angle });
          currentAngle += angle;
        }

        sectorAngles[key] = angles;
      });

      // Propagate angles from outer to inner rings
      subspaces.forEach(key => {
        const dimensions = key.length;
        const parentKey = subspaces.find(k => k.length === dimensions + 1);

        // Skip if this is the outermost ring or no parent
        if (!parentKey || !sectorAngles[parentKey]) return;

        const parentAngles = sectorAngles[parentKey];
        const sectors = 2 ** dimensions;
        const angles = [];

        // For each sector in this ring
        for (let i = 0; i < sectors; i++) {
          const startSector = i * 2;
          const endSector = startSector + 1;

          // Merge angles from parent sectors
          if (startSector < parentAngles.length && endSector < parentAngles.length) {
            angles.push({
              start: parentAngles[startSector].start,
              end: parentAngles[endSector].end
            });
          } else if (startSector < parentAngles.length) {
            angles.push(parentAngles[startSector]);
          } else {
            // Fallback
            const angle = (2 * Math.PI) / sectors;
            angles.push({
              start: (Math.PI / 2) + (i * angle),
              end: (Math.PI / 2) + ((i + 1) * angle)
            });
          }
        }

        sectorAngles[key] = angles;
      });

      return sectorAngles;
    };

    // Point positioning function (reused across views)
    const positionPoint = (point, angle, radius) => {
      return {
        x: radius * Math.cos(angle),
        y: radius * Math.sin(angle)
      };
    };

    // Draw a single point
    const drawPoint = (point, pos, ringKey, index) => {
      // Store point positions for connections
      point.Point_ID.forEach(id => {
        if (!pointPositions[id]) pointPositions[id] = [];
        pointPositions[id].push({
          x: pos.x,
          y: pos.y,
          subspaceId: ringKey
        });
      });

      g.append("circle")
        .attr("cx", pos.x)
        .attr("cy", pos.y)
        .attr("r", 3)
        .attr("fill", "black")
        .attr("stroke", "white")
        .attr("stroke-width", 0.5)
        .style("pointer-events", "visible")
        .on("mouseover", (event) => {
          const pointIds = point.Point_ID.join(", ");

          // Find labels efficiently
          let associatedLabels = [];
          if (labelsData && labelsData.labels) {
            Object.entries(labelsData.labels).forEach(([label, pointList]) => {
              if (point.Point_ID.some(id => pointList.includes(Number(id)))) {
                associatedLabels.push(label);
              }
            });
          }
          const labelText = associatedLabels.length > 0 ? associatedLabels.join(", ") : "No Label";

          tooltip
            .style("visibility", "visible")
            .html(
              `Point_IDs: ${pointIds}<br>Coordinates: (${pos.x.toFixed(2)}, ${pos.y.toFixed(2)})<br>Subspace: ${ringKey}<br>Label: ${labelText}`
            );

          setHoveredCoordinates({ ...point, label: labelText });
        })
        .on("mousemove", (event) => {
          tooltip
            .style("top", event.pageY + 10 + "px")
            .style("left", event.pageX + 10 + "px");
        })
        .on("mouseout", () => {
          tooltip.style("visibility", "hidden");
          setHoveredCoordinates(null);
        });
    };

    // Render normal view
    const renderNormalView = () => {
      // Reuse point positions for line connections
      const pointPositions = {};

      subspaces.forEach((key, index) => {
        if (!ringVisibility[key]) return;

        const innerRadius = (index / subspaces.length) * maxRadius;
        const outerRadius = ((index + 1) / subspaces.length) * maxRadius;
        const ringData = pointsData[index];
        const sectors = 2 ** ringData.dimensions;
        const sectorAngle = 2 * Math.PI / sectors;
        const rotationOffset = Math.PI / 2;

        // Draw sectors
        for (let i = 0; i < sectors; i++) {
          const startAngle = rotationOffset + (sectorAngle * i);
          const endAngle = startAngle + sectorAngle;

          g.append("path")
            .attr("d", d3.arc()
              .innerRadius(innerRadius)
              .outerRadius(outerRadius)
              .startAngle(startAngle)
              .endAngle(endAngle)
            )
            .attr("fill", getSectorColor(index, i))
            .attr("fill-opacity", 0.3)
            .attr("stroke", "black")
            .attr("stroke-width", 0.5);
        }

        // Draw ring label
        g.append("text")
          .attr("x", 0)
          .attr("y", -outerRadius - 5)
          .attr("text-anchor", "middle")
          .attr("font-size", "16px")
          .attr("fill", "red")
          .attr("font-weight", "bold")
          .text(ringLabels[index]);

        // Draw points
        ringData.points.forEach((point, pIndex) => {
          const sectorIndex = Math.min(point.sectorIndex, sectors - 1);
          const centerAngle = rotationOffset + (sectorAngle * sectorIndex) + (sectorAngle / 2);

          // Distribute points within the sector
          const clusterFactor = 0.9;
          const totalPoints = ringData.points.length;
          const radius = innerRadius + (clusterFactor * (outerRadius - innerRadius) * (pIndex % totalPoints)) / totalPoints;

          const pos = positionPoint(point, centerAngle, radius);
          drawPoint(point, pos, key, index);
        });
      });

      return pointPositions;
    };

    // Render proportional view
    const renderProportionalView = () => {
      const sectorAngles = calculateSectorAngles();
      // Reuse point positions for line connections
      const pointPositions = {};

      subspaces.forEach((key, index) => {
        if (!ringVisibility[key]) return;

        const innerRadius = (index / subspaces.length) * maxRadius;
        const outerRadius = ((index + 1) / subspaces.length) * maxRadius;
        const ringData = pointsData[index];
        const angles = sectorAngles[key] || [];

        // Draw sectors
        angles.forEach((sector, i) => {
          g.append("path")
            .attr("d", d3.arc()
              .innerRadius(innerRadius)
              .outerRadius(outerRadius)
              .startAngle(sector.start)
              .endAngle(sector.end)
            )
            .attr("fill", getSectorColor(index, i))
            .attr("fill-opacity", 0.3)
            .attr("stroke", "black")
            .attr("stroke-width", 0.5);
        });

        // Draw ring label
        g.append("text")
          .attr("x", 0)
          .attr("y", -outerRadius - 5)
          .attr("text-anchor", "middle")
          .attr("font-size", "16px")
          .attr("fill", "red")
          .attr("font-weight", "bold")
          .text(ringLabels[index]);

        // Draw points
        ringData.points.forEach((point, pIndex) => {
          const sectorIndex = Math.min(point.sectorIndex, angles.length - 1);
          if (!angles[sectorIndex]) return;

          const sector = angles[sectorIndex];
          const centerAngle = sector.start + ((sector.end - sector.start) / 2);

          // Distribute points within the sector
          const clusterFactor = 0.9;
          const totalPoints = ringData.points.length;
          const radius = innerRadius + (clusterFactor * (outerRadius - innerRadius) * (pIndex % totalPoints)) / totalPoints;

          const pos = positionPoint(point, centerAngle, radius);
          drawPoint(point, pos, key, index);
        });
      });

      return pointPositions;
    };

    // Create a shared pointPositions object
    const pointPositions = {};

    // Render based on view mode
    if (viewMode === "normal") {
      renderNormalView();
    } else if (viewMode === "proportional") {
      renderProportionalView();
    }

    // Draw connections between related points
    Object.entries(pointPositions).forEach(([pointId, positions]) => {
      if (positions.length > 1) {
        // Batch line creation to reduce DOM operations
        const lineData = [];
        for (let i = 0; i < positions.length - 1; i++) {
          lineData.push({
            x1: positions[i].x,
            y1: positions[i].y,
            x2: positions[i + 1].x,
            y2: positions[i + 1].y
          });
        }

        // Draw lines as a group
        g.selectAll("line.connection")
          .data(lineData)
          .enter()
          .append("line")
          .attr("class", "connection")
          .attr("x1", d => d.x1)
          .attr("y1", d => d.y1)
          .attr("x2", d => d.x2)
          .attr("y2", d => d.y2)
          .attr("stroke", getLabelColor(pointId))
          .attr("stroke-width", 1.5)
          .attr("stroke-opacity", 0.9)
          .on("mouseover", () => {
            tooltip
              .style("visibility", "visible")
              .html(`Connection: Point_ID ${pointId}`);
          })
          .on("mousemove", (event) => {
            tooltip
              .style("top", event.pageY + 10 + "px")
              .style("left", event.pageX + 10 + "px");
          })
          .on("mouseout", () => {
            tooltip.style("visibility", "hidden");
          });
      }
    });

    // Add zoom functionality
    const zoom = d3.zoom().on("zoom", (event) => {
      g.attr("transform", event.transform);
    });
    svg.call(zoom);

    return () => {
      tooltip.remove();
    };
  }, [jsonData, labelsData, ringVisibility, setHoveredCoordinates, viewMode]);

  return (
    <div style={{ width: "100%", height: "100%" }}>
      <div style={{ marginBottom: "10px" }}>
        <button
          onClick={() => setViewMode("normal")}
          style={{
            marginRight: "10px",
            padding: "5px 10px",
            backgroundColor: viewMode === "normal" ? "#4CAF50" : "#f0f0f0",
            color: viewMode === "normal" ? "white" : "black",
          }}
        >
          Normal View
        </button>
        <button
          onClick={() => setViewMode("proportional")}
          style={{
            padding: "5px 10px",
            backgroundColor: viewMode === "proportional" ? "#4CAF50" : "#f0f0f0",
            color: viewMode === "proportional" ? "white" : "black",
          }}
        >
          Proportional View
        </button>
      </div>
      <svg ref={graphRef} style={{ width: "100%", height: "800px" }}></svg>
    </div>
  );
};

export default HierarchicalGraph;
