import React, { useState, useCallback } from "react";

const JsonDisplay = ({ jsonData }) => {
  if (!jsonData) return null;

  return (
    <div className="bg-white shadow-lg rounded-lg p-6 mt-4 w-full max-w-3xl">
      <h2 className="text-2xl font-bold mb-4 text-blue-800">Processed Data</h2>
      <pre className="bg-gray-100 p-4 rounded-lg overflow-auto text-sm whitespace-pre-wrap">
        {JSON.stringify(jsonData, null, 2)}
      </pre>
    </div>
  );
};

const App = () => {
  const [jsonData, setJsonData] = useState(null);
  const [error, setError] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  const handleFileUpload = useCallback(async (event) => {
    const file = event.target.files[0];
    if (!file) {
      setError("No file selected");
      return;
    }

    setIsLoading(true);
    setError(null);

    const formData = new FormData();
    formData.append("file", file);

    try {
      // First, upload the file
      const uploadResponse = await fetch("http://127.0.0.1:5000/upload", {
        method: "POST",
        body: formData,
      });

      if (!uploadResponse.ok) {
        throw new Error(`Upload failed: ${uploadResponse.statusText}`);
      }

      const uploadResult = await uploadResponse.json();
      console.log("Upload result:", uploadResult);

      // Then immediately fetch the processed data
      const jsonResponse = await fetch(`http://127.0.0.1:5000/public/${uploadResult.json_filename}`);

      if (!jsonResponse.ok) {
        throw new Error(`Failed to fetch processed data: ${jsonResponse.statusText}`);
      }

      const processedData = await jsonResponse.json();
      console.log("Processed data:", processedData);

      setJsonData(processedData);
      setError(null);

      // Clear the file input
      event.target.value = '';

    } catch (err) {
      console.error("Error during upload/processing:", err);
      setError(err.message);
      setJsonData(null);
    } finally {
      setIsLoading(false);
    }
  }, []);

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gray-100 p-6">
      <h1 className="text-4xl font-bold mb-6 text-blue-800">File Upload</h1>

      <div className="bg-white p-6 rounded-lg shadow-lg w-full max-w-md">
        <div className="flex flex-col space-y-4">
          <label className="flex flex-col space-y-2">
            <span className="text-sm font-medium text-gray-700">Select CSV File</span>
            <input
              type="file"
              accept=".csv"
              onChange={handleFileUpload}
              disabled={isLoading}
              className="border p-2 rounded text-sm"
            />
          </label>

          {isLoading && (
            <div className="text-blue-600 text-sm">
              Processing your file...
            </div>
          )}

          {error && (
            <div className="text-red-500 text-sm">
              {error}
            </div>
          )}
        </div>
      </div>

      {jsonData && (
        <div className="mt-6 w-full max-w-4xl">
          <JsonDisplay jsonData={jsonData} />
        </div>
      )}
    </div>
  );
};

export default App;
