import React, { useEffect, useRef } from "react";
import * as d3 from "d3";

const ScatterPlot = ({ data, labelsData }) => {
    const svgRef = useRef(null);

    useEffect(() => {
        if (!data || !data.points || !Array.isArray(data.points)) return;

        const svg = d3.select(svgRef.current);
        svg.selectAll("*").remove();

        const width = 500;
        const height = 400;
        const margin = { top: 20, right: 20, bottom: 40, left: 40 };

        const g = svg
            .attr("width", width)
            .attr("height", height)
            .append("g")
            .attr("transform", `translate(${margin.left}, ${margin.top})`);

        const xScale = d3
            .scaleLinear()
            .domain(d3.extent(data.points, d => d.x))
            .range([0, width - margin.left - margin.right]);

        const yScale = d3
            .scaleLinear()
            .domain(d3.extent(data.points, d => d.y))
            .range([height - margin.top - margin.bottom, 0]);

        const colorScale = d3
            .scaleOrdinal(d3.schemeCategory10)
            .domain(Object.keys(labelsData?.labels || {}));

        const getLabelColor = (id) => {
            if (!labelsData || !labelsData.labels) return "gray";
            for (const [label, ids] of Object.entries(labelsData.labels)) {
                if (ids.includes(Number(id))) return colorScale(label);
            }
            return "gray";
        };

        // Axes
        g.append("g")
            .attr("transform", `translate(0, ${height - margin.top - margin.bottom})`)
            .call(d3.axisBottom(xScale));
        g.append("g")
            .call(d3.axisLeft(yScale));

        // Points
        g.selectAll("circle")
            .data(data.points)
            .enter()
            .append("circle")
            .attr("cx", d => xScale(d.x))
            .attr("cy", d => yScale(d.y))
            .attr("r", 5)
            .attr("fill", d => getLabelColor(d.id))
            .attr("stroke", "black")
            .attr("stroke-width", 0.5);
    }, [data, labelsData]);

    return (
        <div style={{ width: "100%", height: "400px" }}>
            <svg ref={svgRef}></svg>
        </div>
    );
};

export default ScatterPlot;
