import React, { useEffect, useRef } from "react";
import * as d3 from "d3";

const HierarchicalGraph = ({ jsonData }) => {
  const graphRef = useRef(null);

  useEffect(() => {
    if (!jsonData || Object.keys(jsonData).length === 0) return;

    const subspaces = Object.keys(jsonData);
    const pointsData = subspaces.map((key) => ({
      key,
      points: jsonData[key],
      dimensions: key.length, // Length of the key determines the dimensions
    }));

    const svg = d3.select(graphRef.current);
    const width = 600;
    const height = 600;
    const margin = 20;

    svg.selectAll("*").remove(); // Clear previous content

    const maxSquareSize = Math.min(width, height) - 2 * margin;

    const g = svg
      .attr("width", width)
      .attr("height", height)
      .append("g")
      .attr("transform", `translate(${width / 2}, ${height / 2})`);

    pointsData.forEach((subspace, index) => {
      const squareSize =
        maxSquareSize - 2 * index * (maxSquareSize / subspaces.length);
      const sectors = 2 ** subspace.dimensions; // Number of sectors
      const sectorAngle = (2 * Math.PI) / sectors;

      // Draw square for the subspace
      g.append("rect")
        .attr("x", -squareSize / 2)
        .attr("y", -squareSize / 2)
        .attr("width", squareSize)
        .attr("height", squareSize)
        .attr("stroke", "black")
        .attr("fill", "none")
        .attr("stroke-width", 1);

      // Draw sector lines
      for (let i = 0; i < sectors; i++) {
        const angle = sectorAngle * i;
        const x1 = (squareSize / 2) * Math.cos(angle);
        const y1 = (squareSize / 2) * Math.sin(angle);

        g.append("line")
          .attr("x1", 0)
          .attr("y1", 0)
          .attr("x2", x1)
          .attr("y2", y1)
          .attr("stroke", "gray")
          .attr("stroke-width", 1);
      }

      // Scale for positioning points
      const scale = d3
        .scaleLinear()
        .domain([-1, 1])
        .range([-squareSize / 2, squareSize / 2]);

      // Plot points correctly within their respective subspace and sector
      subspace.points.forEach((point) => {
        const bitPattern = point.Point_ID.toString(2).padStart(
          subspace.dimensions,
          "0"
        );
        const sectorIndex = parseInt(bitPattern, 2);
        const angle = sectorIndex * sectorAngle;

        // Adjust placement within the correct subspace and sector
        const radius =
          (squareSize / 4) *
          (Math.sqrt(point.x ** 2 + (point.y || 0) ** 2) / Math.sqrt(2));
        const scaledX =
          (squareSize / 2) * Math.cos(angle) * 0.8 + radius * Math.cos(angle);
        const scaledY =
          (squareSize / 2) * Math.sin(angle) * 0.8 + radius * Math.sin(angle);

        g.append("circle")
          .attr("cx", scaledX)
          .attr("cy", scaledY)
          .attr("r", 4)
          .attr("fill", "steelblue")
          .attr("stroke", "white")
          .attr("stroke-width", 1)
          .append("title")
          .text(`ID: ${point.Point_ID}`);
      });
    });
  }, [jsonData]);

  return (
    <svg
      ref={graphRef}
      style={{ width: "600px", height: "600px", border: "1px solid black" }}
    ></svg>
  );
};

export default HierarchicalGraph;
