import React, { useState } from "react";

const JsonDisplay = ({ jsonData }) => {
  if (!jsonData) return null;

  return (
    <div className="bg-white shadow-lg rounded-lg p-6 mt-4 w-full max-w-3xl">
      <h2 className="text-2xl font-bold mb-4 text-blue-800">Processed Data</h2>
      <pre className="bg-gray-100 p-4 rounded-lg overflow-auto text-sm whitespace-pre-wrap">
        {JSON.stringify(jsonData, null, 2)}
      </pre>
    </div>
  );
};

const App = () => {
  const [jsonData, setJsonData] = useState(null);
  const [error, setError] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [selectedFileName, setSelectedFileName] = useState("");

  const handleFileSelect = async (event) => {
    const file = event.target.files[0];
    if (!file) return;

    setSelectedFileName(file.name);
    setIsLoading(true);
    setError(null);

    const formData = new FormData();
    formData.append("file", file);

    try {
      const uploadResponse = await fetch("http://127.0.0.1:5000/upload", {
        method: "POST",
        body: formData,
      });

      if (!uploadResponse.ok) {
        throw new Error("Upload failed");
      }

      const result = await uploadResponse.json();
      console.log("Upload result:", result);

      // Fetch the processed JSON data
      const jsonResponse = await fetch(`http://127.0.0.1:5000/public/${result.json_filename}`);
      if (!jsonResponse.ok) {
        throw new Error("Failed to fetch processed data");
      }

      const processedData = await jsonResponse.json();
      console.log("Processed data:", processedData);
      setJsonData(processedData);

    } catch (err) {
      console.error("Error:", err);
      setError(err.message);
      setJsonData(null);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gray-100 p-6">
      <h1 className="text-4xl font-bold mb-6 text-blue-800">File Upload</h1>

      <div className="bg-white p-6 rounded-lg shadow-lg w-full max-w-md">
        <div className="space-y-4">
          <div className="flex flex-col space-y-2">
            <label className="text-sm font-medium text-gray-700">
              Select CSV File
            </label>
            <input
              type="file"
              accept=".csv"
              onChange={handleFileSelect}
              disabled={isLoading}
              className="border p-2 rounded text-sm"
            />
          </div>

          {selectedFileName && (
            <div className="text-sm text-gray-600 bg-gray-50 p-2 rounded">
              Current file: {selectedFileName}
            </div>
          )}

          {isLoading && (
            <div className="text-blue-600 text-sm animate-pulse">
              Processing {selectedFileName}...
            </div>
          )}

          {error && (
            <div className="text-red-500 text-sm p-2 bg-red-50 rounded">
              Error: {error}
            </div>
          )}
        </div>
      </div>

      {jsonData && <JsonDisplay jsonData={jsonData} />}
    </div>
  );
};

export default App;
