import React, { useState } from "react";
import HierarchicalGraph from "./JsonDisplay";

const App = () => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [fileName, setFileName] = useState("");
  const [jsonData, setJsonData] = useState(null);
  const [error, setError] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [hoveredCoordinates, setHoveredCoordinates] = useState([]); // Array to store multiple hovered points

  // File selection handler
  const handleFileSelect = (e) => {
    const file = e.target.files[0];
    if (file) {
      setSelectedFile(file);
      setFileName(file.name);
      setError(null);
    }
  };

  // Upload handler
  const handleUpload = async (e) => {
    e.preventDefault();

    if (!selectedFile) {
      setError("Please select a file first");
      return;
    }

    setIsLoading(true);
    const formData = new FormData();
    formData.append("file", selectedFile);

    try {
      const uploadResponse = await fetch("http://127.0.0.1:5000/upload", {
        method: "POST",
        body: formData,
      });

      if (!uploadResponse.ok) {
        throw new Error("Upload failed");
      }

      const result = await uploadResponse.json();
      console.log("Upload successful:", result);

      // Fetch processed data
      const jsonResponse = await fetch(
        `http://127.0.0.1:5000/public/${result.json_filename}`
      );
      const processedData = await jsonResponse.json();
      setJsonData(processedData);
    } catch (err) {
      setError(err.message);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="h-screen bg-gray-100 p-6 flex">
      {/* Sidebar with CSV input */}
      <div className="w-1/4 p-4 bg-white shadow rounded-lg">
        <h1 className="text-xl font-bold mb-4">File Upload</h1>

        <div className="mb-4">
          <input
            type="file"
            accept=".csv"
            onChange={handleFileSelect}
            className="mb-2 w-full"
            disabled={isLoading}
          />

          {fileName && (
            <div className="mt-2 p-2 bg-blue-50 rounded">
              Selected file: <span className="font-semibold">{fileName}</span>
            </div>
          )}
        </div>

        <button
          type="button"
          onClick={handleUpload}
          disabled={!selectedFile || isLoading}
          className="bg-blue-500 text-white px-4 py-2 rounded 
                   disabled:bg-blue-300 disabled:cursor-not-allowed w-full"
        >
          {isLoading ? "Processing..." : "Upload"}
        </button>

        {error && <div className="mt-2 text-red-500">{error}</div>}

        {isLoading && (
          <div className="mt-2 text-blue-500">Processing your file...</div>
        )}

        <div className="my-12">
          {/* Display hovered coordinates */}
          {hoveredCoordinates.length > 0 && (
            <div className="mb-4 p-2 bg-green-50 rounded text-2xl">
              <strong>Hovered Nodes:</strong>
              {hoveredCoordinates.map((coord, index) => (
                <div key={index} className="mb-2 p-2 border-b">
                  <div>ID: {coord.id}</div>
                  {Object.entries(coord)
                    .filter(([key]) => key !== "id")
                    .map(([key, value]) => (
                      <div key={key}>
                        <strong>{key}:</strong>{" "}
                        {value.toFixed ? value.toFixed(2) : value}
                      </div>
                    ))}
                </div>
              ))}
            </div>
          )}
        </div>
      </div>

      {/* Main content area */}
      <div className="h-full flex-1 flex flex-col items-center justify-center">
        <h2 className="text-2xl font-bold mb-4">JSON Data</h2>
        <div
          className="flex justify-center items-center bg-white shadow rounded-lg p-4"
          style={{ width: "1400px", height: "1000px" }}
        >
          {jsonData && (
            <HierarchicalGraph
              jsonData={jsonData}
              setHoveredCoordinates={(hoveredData) => {
                setHoveredCoordinates((prev) => [...prev, hoveredData]); // Append new hovered data
              }}
            />
          )}
        </div>
      </div>
    </div>
  );
};

export default App;
