import React, { useEffect, useRef } from "react";
import * as d3 from "d3";

const HierarchicalGraph = ({ jsonData, setHoveredCoordinates }) => {
  const graphRef = useRef(null);

  useEffect(() => {
    if (!jsonData || jsonData.length === 0) return;

    // Dynamically extract coordinate keys (dimensions)
    const coordinateKeys = Object.keys(jsonData[0]).filter(
      (key) => key !== "Cluster" && key !== "Bead"
    );

    // Helper to calculate centroid
    const calculateCentroid = (points) => {
      const n = points.length;
      const sum = points.reduce((acc, point) => {
        coordinateKeys.forEach((key) => {
          acc[key] = (acc[key] || 0) + point[key];
        });
        return acc;
      }, {});
      // Compute average for each dimension
      Object.keys(sum).forEach((key) => (sum[key] /= n));
      return sum;
    };
    // Assign bit vectors based on coordinate keys (planes)
    const assignBitVector = (point) => {
      return coordinateKeys
        .map((key) => (point[key] >= 0 ? "1" : "0"))
        .join("");
    };
    // Parse and process data
    const clusters = d3.group(jsonData, (d) => d.Cluster);
    const beads = d3.group(jsonData, (d) => `${d.Cluster}-${d.Bead}`);

    // Root centroid
    const rootCentroid = calculateCentroid(jsonData);

    // Cluster centroids
    const clusterCentroids = Array.from(clusters, ([cluster, points]) => ({
      cluster,
      centroid: calculateCentroid(points),
    }));

    // Bead centroids
    const beadCentroids = Array.from(beads, ([key, points]) => {
      const [cluster, bead] = key.split("-");
      return {
        cluster,
        bead,
        centroid: calculateCentroid(points),
        points,
      };
    });

    // Build hierarchical links
    const links = [];
    const nodes = [
      {
        id: "root",
        ...rootCentroid,
        bitVector: assignBitVector(rootCentroid),
        isCentroid: true,
      }, // Root node
    ];

    // Add cluster nodes and links
    clusterCentroids.forEach(({ cluster, centroid }) => {
      nodes.push({
        id: `cluster-${cluster}`,
        ...centroid,
        bitVector: assignBitVector(centroid),
        isCentroid: true,
      });
      links.push({ source: "root", target: `cluster-${cluster}` });
    });

    // Add bead nodes and links
    beadCentroids.forEach(({ cluster, bead, centroid, points }) => {
      nodes.push({
        id: `bead-${cluster}-${bead}`,
        ...centroid,
        bitVector: assignBitVector(centroid),
        isCentroid: true,
      });
      links.push({
        source: `cluster-${cluster}`,
        target: `bead-${cluster}-${bead}`,
      });

      // Add individual points and links
      points.forEach((point, index) => {
        const pointId = `point-${cluster}-${bead}-${index}`;
        nodes.push({
          id: pointId,
          ...point,
          bitVector: assignBitVector(point),
          isCentroid: false,
        });
        links.push({ source: `bead-${cluster}-${bead}`, target: pointId });
      });
    });

    // Set up container dimensions
    const container = d3.select(graphRef.current.parentNode);
    const width = container.node().getBoundingClientRect().width;
    const height = container.node().getBoundingClientRect().height;
    const margin = { top: 20, right: 20, bottom: 20, left: 20 };

    const svg = d3
      .select(graphRef.current)
      .attr("width", width)
      .attr("height", height)
      .attr("viewBox", `0 0 ${width} ${height}`)
      .attr("preserveAspectRatio", "xMidYMid meet");

    // Clear previous render
    svg.selectAll("*").remove();

    // Calculate the number of quadrants based on the number of dimensions
    const numDimensions = coordinateKeys.length;
    const numQuadrants = 2 ** numDimensions;

    // Calculate initial positions based on bit vectors
    nodes.forEach((node) => {
      if (node.bitVector) {
        // Convert the bit vector to a decimal number
        const quadrantIndex = parseInt(node.bitVector, 2);

        // Calculate quadrant size and starting position
        const numRows = Math.ceil(Math.sqrt(numQuadrants));
        const numCols = Math.ceil(numQuadrants / numRows);
        const quadrantWidth = (width - margin.left - margin.right) / numCols;
        const quadrantHeight = (height - margin.top - margin.bottom) / numRows;

        const row = Math.floor(quadrantIndex / numCols);
        const col = quadrantIndex % numCols;

        const x = col * quadrantWidth + quadrantWidth / 2 + margin.left;
        const y = row * quadrantHeight + quadrantHeight / 2 + margin.top;

        node.x = x;
        node.y = y;
      }
    });

    // Draw grid lines and text
    const numRows = Math.ceil(Math.sqrt(numQuadrants));
    const numCols = Math.ceil(numQuadrants / numRows);
    const quadrantWidth = (width - margin.left - margin.right) / numCols;
    const quadrantHeight = (height - margin.top - margin.bottom) / numRows;

    const grid = svg.append("g").attr("class", "grid");

    // Draw vertical lines
    for (let i = 0; i <= numCols; i++) {
      grid
        .append("line")
        .attr("x1", margin.left + i * quadrantWidth)
        .attr("y1", margin.top)
        .attr("x2", margin.left + i * quadrantWidth)
        .attr("y2", height - margin.bottom)
        .attr("stroke", "#ddd")
        .attr("stroke-width", 1);
    }

    // Draw horizontal lines
    for (let i = 0; i <= numRows; i++) {
      grid
        .append("line")
        .attr("x1", margin.left)
        .attr("y1", margin.top + i * quadrantHeight)
        .attr("x2", width - margin.right)
        .attr("y2", margin.top + i * quadrantHeight)
        .attr("stroke", "#ddd")
        .attr("stroke-width", 1);
    }
    // Add bit vector text
    for (let i = 0; i < numQuadrants; i++) {
      const row = Math.floor(i / numCols);
      const col = i % numCols;

      const x = col * quadrantWidth + quadrantWidth / 2 + margin.left;
      const y = row * quadrantHeight + quadrantHeight / 2 + margin.top;

      const bitVector = i.toString(2).padStart(numDimensions, "0");

      grid
        .append("text")
        .attr("x", x)
        .attr("y", y)
        .attr("text-anchor", "middle")
        .attr("dominant-baseline", "middle")
        .attr("font-size", "10px")
        .text(bitVector);
    }

    // Force simulation
    const simulation = d3
      .forceSimulation(nodes)
      .force(
        "link",
        d3
          .forceLink(links)
          .id((d) => d.id)
          .distance(-10)
      )
      .force(
        "charge",
        d3.forceManyBody().strength((d) => (d.isCentroid ? -20 : 1))
      )
      .force("center", d3.forceCenter(width / 2, height / 2))
      .force(
        "collision",
        d3.forceCollide().radius((d) => (d.isCentroid ? 10 : 5))
      )
      .force(
        "center",
        d3.forceCenter(
          (width - margin.left - margin.right) / 2,
          (height - margin.top - margin.bottom) / 2
        )
      )
      .on("tick", () => {
        link
          .attr("x1", (d) =>
            clamp(d.source.x, margin.left, width - margin.right)
          )
          .attr("y1", (d) =>
            clamp(d.source.y, margin.top, height - margin.bottom)
          )
          .attr("x2", (d) =>
            clamp(d.target.x, margin.left, width - margin.right)
          )
          .attr("y2", (d) =>
            clamp(d.target.y, margin.top, height - margin.bottom)
          );

        node
          .attr("cx", (d) => clamp(d.x, margin.left, width - margin.right))
          .attr("cy", (d) => clamp(d.y, margin.top, height - margin.bottom));
      });
    const clamp = (value, min, max) => Math.max(min, Math.min(value, max));
    // Draw links
    const link = svg
      .append("g")
      .attr("class", "links")
      .selectAll("line")
      .data(links)
      .enter()
      .append("line")
      .attr("stroke", "#aaa")
      .attr("stroke-width", 2);

    // Draw nodes
    const node = svg
      .append("g")
      .attr("class", "nodes")
      .selectAll("circle")
      .data(nodes)
      .enter()
      .append("circle")
      .attr("r", (d) => (d.isCentroid ? 10 : 5))
      .attr("fill", (d) => {
        if (d.id === "root") return "red";
        if (d.id.startsWith("cluster")) return "blue";
        if (d.id.startsWith("bead")) return "green";
        return "gray";
      })
      .on("mouseover", (event, d) => {
        const coordinates = {};
        coordinateKeys.forEach((key) => {
          coordinates[key] = d[key];
        });

        setHoveredCoordinates({
          ...coordinates,
          id: d.id,
          bitVector: d.bitVector,
        });
      })
      .call(
        d3
          .drag()
          .on("start", (event) => {
            if (!event.active) simulation.alphaTarget(0.3).restart();
            event.subject.fx = event.subject.x;
            event.subject.fy = event.subject.y;
          })
          .on("drag", (event) => {
            event.subject.fx = event.x;
            event.subject.fy = event.y;
          })
          .on("end", (event) => {
            if (!event.active) simulation.alphaTarget(0);
            event.subject.fx = null;
            event.subject.fy = null;
          })
      );

    // Add tooltips
    node.append("title").text((d) => `ID: ${d.id}\nBit Vector: ${d.bitVector}`);
  }, [jsonData, setHoveredCoordinates]);

  return <svg ref={graphRef} style={{ width: "100%", height: "100%" }}></svg>;
};

export default HierarchicalGraph;
