// export default HierarchicalGraph;
const calculateProportionalAngles = () => {
  console.log("Starting calculateProportionalAngles...");
  console.log("pointsData:", pointsData);

  if (pointsData.length === 0) {
    console.error("No points data available");
    return [];
  }

  const lastRing = pointsData[pointsData.length - 1];
  const lastRingPoints = lastRing.points || [];
  const sectorsCount = Math.max(1, 2 ** lastRing.dimensions);
  const totalAngle = 2 * Math.PI;

  // If no points in last ring, distribute angles equally
  if (lastRingPoints.length === 0) {
    console.warn("Last ring has no points, using equal angles");
    return pointsData.map((_, i) => {
      const sectorCount = Math.max(1, 2 ** (i + 1));
      return Array(sectorCount).fill(totalAngle / sectorCount);
    });
  }

  const pointCounts = new Array(sectorsCount).fill(0);
  lastRingPoints.forEach((point) => {
    const pointData = Object.entries(point).filter(([key]) => key !== "Point_ID");
    // Normalize coordinates to binary (0 or 1) based on sign
    const bitVector = pointData.map(([_, coord]) => (coord >= 0 ? 1 : 0)).join("");
    const sectorIndex = Math.min(parseInt(bitVector, 2), sectorsCount - 1); // Cap index
    pointCounts[sectorIndex]++;
  });

  const minAngle = Math.PI / 180; // 1 degree
  const totalNonEmptyPoints = Math.max(1, pointCounts.reduce((sum, count) => sum + count, 0));
  const remainingAngle = totalAngle - sectorsCount * minAngle;

  const sectorAngles = pointCounts.map((count) =>
    count > 0 ? minAngle + (count / totalNonEmptyPoints) * remainingAngle : minAngle
  );

  // Ensure total angle is exactly 2π
  const currentTotal = sectorAngles.reduce((sum, angle) => sum + angle, 0);
  const correctionFactor = totalAngle / currentTotal;
  const adjustedSectorAngles = sectorAngles.map(angle => angle * correctionFactor);

  // Propagate to inner rings
  const ringAngles = [adjustedSectorAngles];
  for (let i = pointsData.length - 2; i >= 0; i--) {
    const prevAngles = ringAngles[0];
    const sectorCount = Math.max(1, 2 ** (i + 1));
    const currAngles = new Array(sectorCount).fill(0);

    for (let j = 0; j < sectorCount; j++) {
      const angle1 = prevAngles[j * 2] || 0;
      const angle2 = prevAngles[j * 2 + 1] || 0;
      currAngles[j] = angle1 + angle2;
    }
    ringAngles.unshift(currAngles);
  }

  console.log("Final ring angles:", ringAngles);
  return ringAngles;
};

const renderProportionalView = () => {
  const ringAngles = calculateProportionalAngles();
  console.log("Rendering proportional view with ringAngles:", ringAngles);

  if (!ringAngles || ringAngles.length !== pointsData.length || ringAngles.some(arr => !arr || !Array.isArray(arr))) {
    console.error("Invalid ring angles:", ringAngles);
    renderNormalView(); // Fallback with explicit logging
    return;
  }

  subspaces.forEach((key, index) => {
    if (!ringVisibility[key]) return;
    const innerRadius = (index / subspaces.length) * maxRadius;
    const outerRadius = ((index + 1) / subspaces.length) * maxRadius;
    let currentAngle = Math.PI / 2;

    ringAngles[index].forEach((angle, i) => {
      const startAngle = currentAngle;
      const endAngle = currentAngle + angle;

      g.append("path")
        .attr("d", d3.arc()
          .innerRadius(innerRadius)
          .outerRadius(outerRadius)
          .startAngle(startAngle)
          .endAngle(endAngle)
        )
        .attr("fill", getSectorColor(index, i))
        .attr("fill-opacity", 0.3)
        .attr("stroke", "black")
        .attr("stroke-width", 0.5)
        .style("cursor", "pointer");

      currentAngle = endAngle;
    });

    g.append("text")
      .attr("x", 0)
      .attr("y", -outerRadius - 5)
      .attr("text-anchor", "middle")
      .attr("font-size", "16px")
      .attr("fill", "red")
      .attr("font-weight", "bold")
      .text(ringLabels[index]);

    renderPoints(index, innerRadius, outerRadius, ringAngles[index]);
  });
};
